void ruby()
{
    int x = 0;
    int y = (kMatrixHeight / 2) - 4 ; //This is our starting position. Our 16-high x 32-wide string starts 4 chars above the middle
    int z = 0;
    x = corkScrew_x;
    if (x >= kMatrixWidth) {x = 0;}

       
    for(int ay = 0; ay < 16; ay++)
    {
      for(int ax = 0; ax < 64; ax++)
      {
        z = (x-ax);
        if (z < 0 ) {z += kMatrixWidth ;} // Wrap around the "join" of the ball
        leds[ XY (z, (y+ay)) ] = pgm_read_dword(&(RUBY_text[((ay*64)+ax)]));  // Read array from Flash
      }
    }
    corkScrew_x++;
    if (corkScrew_x >= kMatrixWidth)
    {
      corkScrew_x = 0;
    }
}
